import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Button, Input, Table, Form as Forms, Label } from "reactstrap";

import { greedy } from "../greedy";

function TableItem(props) {
  const [items, setItems] = useState([]);
  const [weight, setWeight] = useState({
    value: "",
    touched: false,
    errors: "",
  });
  const [errors, setErrors] = useState([]);
  const [touched, setTouched] = useState([]);

  const initItem = {
    name: "",
    value: "",
    weight: "",
    stock: "",
    qty: "",
    time: "",
  };

  // useEffect(() => {
  //   console.log(errors);
  // }, [errors]);

  const addItem = () => {
    setItems([...items, initItem]);
    setErrors([...errors, initItem]);
  };

  const handleWeightChange = (e) => {
    setWeight({ ...weight, value: e.target.value, errors: "" });
  };

  const handleChange = (index) => (e) => {
    let newArr = items.map((item, i) => {
      if (index == i) {
        return { ...item, [e.target.name]: e.target.value };
      } else {
        return item;
      }
    });
    setItems(newArr);

    let newErr = errors.map((error, i) => {
      if (index == i) {
        return { ...error, [e.target.name]: "" };
      } else {
        return error;
      }
    });
    setErrors(newErr);
  };

  const handleBlur = (index) => (e) => {
    if (!e.target.value) {
      let newErr = errors.map((error, i) => {
        if (index == i) {
          return { ...error, [e.target.name]: "Required" };
        } else {
          return error;
        }
      });
      setErrors(newErr);
    }
  };
  const handleWeightBlur = (e) => {
    setWeight({ ...weight, touched: true });
    if (!e.target.value) {
      setWeight({ ...weight, errors: "Required" });
    }
  };

  const checkError = () => {
    if (weight.errors !== "") return true;
    for (var i = 0; i < errors.length; i++) {
      if (errors[i].name !== "") return true;
      if (errors[i].value !== "") return true;
      if (errors[i].weight !== "") return true;
      if (errors[i].stock !== "") return true;
    }
    return false;
  };

  const checkValue = () => {
    if (weight.value === "") {
      setWeight({ ...weight, errors: "Required" });
    }
    const newErrors = [...errors];

    for (var i = 0; i < items.length; i++) {
      let error = { ...newErrors[i] };
      if (items[i].name === "") {
        error.name = "Required";
      }
      if (items[i].value === "") {
        error.value = "Required";
      }
      if (items[i].weight === "") {
        error.weight = "Required";
      }
      if (items[i].stock === "") {
        error.stock = "Required";
      }
      newErrors[i] = error;
    }
    setErrors(newErrors);
  };

  const onSubmit = () => {
    checkValue();
    console.log(checkError());

    //console.log(greedy(items, parseInt(weight.value)));
    setItems(greedy(items, parseInt(weight.value)));
  };

  return (
    <div>
      <Forms>
        <Label for="weight">Weight</Label>
        <Input
          style={{ width: "100px" }}
          type="number"
          name="weight"
          id="weight"
          value={weight.value}
          onChange={handleWeightChange}
          onBlur={handleWeightBlur}
          className={weight.errors && "is-invalid"}
        />
        {weight.errors && (
          <span className="invalid-feedback">{weight.errors}</span>
        )}
        {/* {errors.weight && "Required"} */}
        <Table striped className="table-hover">
          <thead>
            <tr>
              <th style={{ width: "500px" }}>Name</th>
              <th>Value</th>
              <th>Weight</th>
              <th>Stock</th>
              <th>Qty</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>
                  <Input
                    type="text"
                    id="name"
                    value={item.name}
                    name="name"
                    onChange={handleChange(index)}
                    onBlur={handleBlur(index)}
                    className={errors[index].name && "is-invalid"}
                  ></Input>
                  {errors[index].name && (
                    <span className="invalid-feedback">
                      {errors[index].name}
                    </span>
                  )}
                </td>
                <td>
                  <Input
                    type="text"
                    value={item.value}
                    name="value"
                    onChange={handleChange(index)}
                    onBlur={handleBlur(index)}
                    className={errors[index].value && "is-invalid"}
                  ></Input>
                  {errors[index].value && (
                    <span className="invalid-feedback">
                      {errors[index].value}
                    </span>
                  )}
                </td>
                <td>
                  <Input
                    type="text"
                    value={item.weight}
                    name="weight"
                    onChange={handleChange(index)}
                    onBlur={handleBlur(index)}
                    className={errors[index].weight && "is-invalid"}
                  ></Input>
                  {errors[index].weight && (
                    <span className="invalid-feedback">
                      {errors[index].weight}
                    </span>
                  )}
                </td>
                <td>
                  <Input
                    type="text"
                    value={item.stock}
                    name="stock"
                    onChange={handleChange(index)}
                    onBlur={handleBlur(index)}
                    className={errors[index].stock && "is-invalid"}
                  ></Input>
                  {errors[index].stock && (
                    <span className="invalid-feedback">
                      {errors[index].stock}
                    </span>
                  )}
                </td>
                <td>
                  <Input
                    type="text"
                    disabled
                    value={item.qty}
                    name="qty"
                    onChange={handleChange(index)}
                  ></Input>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <Button onClick={() => addItem()}>Add Item</Button>
        <Button onClick={onSubmit}>Submit</Button>
      </Forms>
    </div>
  );
}

TableItem.propTypes = {};

export default TableItem;
