import React from "react";
import classNames from "classnames";
import * as XLSX from "xlsx";
import { FormGroup, Input, Label } from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function InputFile(props) {
  const {
    setFileName,
    fileNames,
    setIsLoading,
    setInputFile,
    file,
    setFile,
    disabled,
  } = props;

  const getExtFile = (filename) => {
    let last_dot = filename.lastIndexOf(".");
    return filename.slice(last_dot + 1);
  };

  const readFileTxt = (reader) => {
    const items = [];

    reader.onload = async (e) => {
      setIsLoading(true);
      console.log("load");
      const text = e.target.result.split(/\r\n|\n/);
      let weight = parseInt(text[0]);
      text.forEach((line, index) => {
        //console.log(line);
        if (index !== 0) {
          const cell = line.split(" ");

          const stock = cell[2] % 1 === 0 ? cell[2] : "";
          let name = "";
          if (stock !== "") {
            cell.slice(3).forEach((t) => {
              name += t + " ";
            });
          } else {
            cell.slice(2).forEach((t) => {
              name += t + " ";
            });
          }
          items.push({
            name: name.trim(),
            value: cell[0],
            weight: cell[1],
            stock: stock,
            qty: "",
            time: "",
          });
        }
      });
      console.log(items);
      setInputFile(weight, items);

      setIsLoading(false);
    };
    //reader.readAsText(e.target.files[0]);
  };

  const readFileXlsx = (reader) => {
    console.log("call");
    reader.onload = async (e) => {
      setIsLoading(true);
      console.log("load");

      /* Parse data */
      const bstr = e.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      /* Get first worksheet */
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      /* Convert array of arrays */
      const data = XLSX.utils.sheet_to_json(ws, { header: 1 });
      /* Update state */
      console.log(data);
      console.log(convertToJson(data));
      //setInputFile(0, convertToJson(data));
      setIsLoading(false);
    };
  };

  const readFile = (e) => {
    if (e !== undefined) {
      if (e.target.files[0] !== undefined) {
        e.preventDefault();
        const items = [];
        const reader = new FileReader();
        let ext = getExtFile(e.target.files[0].name);
        setFileName(e.target.files[0].name);
        setFile(e.target.value);

        switch (ext) {
          case "txt":
            readFileTxt(reader);
            break;

          case "xlsx":
            readFileXlsx(reader);
            break;
        }

        ext === "xlsx" && reader.readAsBinaryString(e.target.files[0]);
        ext === "txt" && reader.readAsText(e.target.files[0]);
      }
    }
  };

  const convertToJson = (csv) => {
    var lines = csv.split("\n");

    var result = [];

    var headers = lines[0].split(",");

    for (var i = 1; i < lines.length; i++) {
      var obj = {};
      var currentline = lines[i].split(",");

      for (var j = 0; j < headers.length; j++) {
        obj[headers[j]] = currentline[j];
      }

      result.push(obj);
    }

    //return result; //JavaScript object
    return JSON.stringify(result); //JSON
  };

  return (
    <FormGroup>
      <legend>File</legend>
      <Label
        for="file"
        // className="sd-file-choose btn btn-primary disabled"
        className={classNames("sd-file-choose btn btn-primary", {
          disabled: disabled,
        })}
      >
        <FontAwesomeIcon icon="file-upload" /> Upload
      </Label>

      <Input
        type="file"
        name="file"
        id="file"
        onChange={(e) => readFile(e)}
        style={{ display: "none" }}
        value={file}
        disabled={disabled}
      />
      <br></br>
      <Label>{fileNames}</Label>
    </FormGroup>
  );
}
