import React, { useState } from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";

const ModalExample = (props) => {
  const { className, title, content } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  return (
    <div>
      <span onClick={toggle}>Trợ Giúp</span>

      <Modal
        isOpen={modal}
        toggle={toggle}
        className={className}
        size="lg"
        style={{ maxWidth: "1600px", width: "80%" }}
      >
        <ModalHeader toggle={toggle}>Trợ Giúp</ModalHeader>
        <ModalBody>
          <ol>
            <li>Chọn khối lượng</li>
            <li>Chọn loại ba lô</li>
            <li>
              Nhập danh sách đồ vật
              <ul>
                <li>Thêm thủ công bằng tay</li>
              </ul>
            </li>
          </ol>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggle}>
            Nice!
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default ModalExample;
