import React from "react";
import { Button, FormGroup, Input, Label } from "reactstrap";
import ModalType1 from "../ModalType/ModalType1";

export default function TypeBalo(props) {
  const { onTypeChange, type } = props;
  return (
    <FormGroup tag="fieldset">
      <legend>Type</legend>
      <FormGroup check>
        <Label check>
          <Input
            type="radio"
            name="radio1"
            value="1"
            onChange={(e) => onTypeChange(e)}
          />{" "}
          Balo1
        </Label>
        <ModalType1 buttonLabel="?" />
      </FormGroup>
      <FormGroup check>
        <Label check>
          <Input
            type="radio"
            name="radio1"
            value="2"
            onChange={(e) => onTypeChange(e)}
          />{" "}
          Balo2
        </Label>
      </FormGroup>
      <FormGroup check>
        <Label check>
          <Input
            type="radio"
            name="radio1"
            value="3"
            onChange={(e) => onTypeChange(e)}
          />{" "}
          Balo3
        </Label>
      </FormGroup>
      <div className={type.errors && "is-invalid"}></div>
      {type.errors && <span className="invalid-feedback">{type.errors}</span>}
    </FormGroup>
  );
}
