import React from "react";
import { Input } from "reactstrap";

export default function InputFile(props) {
  const { setWeightFile, setItemsFile } = props;

  const readFile = (e) => {
    e.preventDefault();
    const items = [];
    const reader = new FileReader();

    reader.onload = async (e) => {
      const text = e.target.result.split(/\r\n|\n/);
      setWeightFile(parseInt(text[0]));
      text.forEach((line, index) => {
        //console.log(line);
        if (index !== 0) {
          const cell = line.split(" ");

          const stock = cell[2] % 1 === 0 ? cell[2] : "";
          let name = "";
          if (stock !== "") {
            cell.slice(3).forEach((t) => {
              name += t + " ";
            });
          } else {
            cell.slice(2).forEach((t) => {
              name += t + " ";
            });
          }
          items.push({
            name: name.trim(),
            value: cell[0],
            weight: cell[1],
            stock: stock,
            qty: "",
            time: "",
          });
        }
      });
      //console.log(items);
      //setItemsFile(items);
    };
    reader.readAsText(e.target.files[0]);
    console.log("out", items);
    setItemsFile(items);
  };

  return (
    <div>
      <Input type="file" name="file" onChange={(e) => readFile(e)} />
    </div>
  );
}
