import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Button, Input, Table, Form as Forms, Label } from "reactstrap";

import { greedy } from "../../greedy";
import InputFile from "./InputFile";

function TableItem(props) {
  const [items, setItems] = useState([]);
  const [weight, setWeight] = useState({
    value: "",
    touched: false,
    errors: "",
  });
  const [errors, setErrors] = useState([]);

  const initItem = {
    name: "",
    value: "",
    weight: "",
    stock: "",
    qty: "",
    time: "",
  };

  useEffect(() => {
    console.log(testItem);
  }, [testItem]);

  const addItem = () => {
    setItems([...items, initItem]);
    setErrors([...errors, initItem]);
  };

  const handleWeightChange = (e) => {
    setWeight({ ...weight, value: e.target.value, errors: "" });
  };

  const handleChange = (e, index) => {
    let newArr = items.map((item, i) => {
      if (index === i) {
        return { ...item, [e.target.name]: e.target.value };
      } else {
        return item;
      }
    });
    setItems(newArr);

    let newErr = errors.map((error, i) => {
      if (index === i) {
        return { ...error, [e.target.name]: "" };
      } else {
        return error;
      }
    });
    setErrors(newErr);
  };

  const handleBlur = (index) => (e) => {
    if (!e.target.value) {
      let newErr = errors.map((error, i) => {
        if (index === i) {
          return { ...error, [e.target.name]: "Required" };
        } else {
          return error;
        }
      });
      setErrors(newErr);
    } else {
      let newErr = errors.map((error, i) => {
        if (
          index === i &&
          e.target.name !== "name" &&
          e.target.value % 1 !== 0
        ) {
          return { ...error, [e.target.name]: "Value must be number" };
        } else {
          if (
            index === i &&
            e.target.name !== "name" &&
            parseInt(e.target.value) <= 0
          ) {
            return { ...error, [e.target.name]: "Value must > 0" };
          } else {
            return error;
          }
        }
      });

      setErrors(newErr);
    }
  };
  const handleWeightBlur = (e) => {
    setWeight({ ...weight, touched: true });
    if (!e.target.value) {
      setWeight({ ...weight, errors: "Required" });
    } else {
      if (e.target.value % 1 !== 0)
        setWeight({ ...weight, errors: "Value must be number" });
      else {
        if (parseInt(e.target.value) <= 0)
          setWeight({ ...weight, errors: "Value must be > 0" });
      }
    }
  };

  const checkValue = () => {
    //false: no error
    //true: have error
    let check = false;
    if (weight.value === "") {
      setWeight({ ...weight, errors: "Required" });
      check = true;
    }
    const newErrors = [...errors];

    for (var i = 0; i < items.length; i++) {
      let error = { ...newErrors[i] };
      if (items[i].name === "") {
        error.name = "Required";
        check = true;
      }
      if (items[i].value === "") {
        error.value = "Required";
        check = true;
      }
      if (items[i].weight === "") {
        error.weight = "Required";
        check = true;
      }
      if (items[i].stock === "") {
        error.stock = "Required";
        check = true;
      }
      newErrors[i] = error;
    }
    setErrors(newErrors);
    return check;
  };

  const onSubmit = () => {
    if (!checkValue()) {
      setItems(greedy(items, parseInt(weight.value)));
    }
    //console.log(greedy(items, parseInt(weight.value)));
  };

  const clearItem = () => {
    setItems([]);
    setWeight({
      value: "",
      touched: false,
      errors: "",
    });
  };

  const setWeightFile = (value) => {
    setWeight({ ...weight, value: value, errors: "" });
    //checkValue();
  };

  const [testItem, setTestItem] = useState();
  const setItemsFile = (inputItems) => {
    console.log(inputItems);
    setTestItem(inputItems);
  };

  return (
    <div>
      <Forms>
        <Label for="weight">Weight</Label>
        <Input
          style={{ width: "100px" }}
          type="number"
          name="weight"
          id="weight"
          value={weight.value}
          onChange={handleWeightChange}
          onBlur={handleWeightBlur}
          className={weight.errors && "is-invalid"}
        />
        {weight.errors && (
          <span className="invalid-feedback">{weight.errors}</span>
        )}
        <InputFile setWeightFile={setWeightFile} setItemsFile={setItemsFile} />
        <Table striped className="table-hover">
          <thead>
            <tr>
              <th style={{ width: "500px" }}>Name</th>
              <th>Value</th>
              <th>Weight</th>
              <th>Stock</th>
              <th>Qty</th>
            </tr>
          </thead>
          {Array.isArray(items) && (
            <tbody>
              {items.map((item, index) => (
                <tr key={index}>
                  <td>
                    <Input
                      type="text"
                      id="name"
                      value={item ? item.name : null}
                      name="name"
                      onChange={(e) => handleChange(e, index)}
                      onBlur={handleBlur(index)}
                      // className={errors[index].name && "is-invalid"}
                    ></Input>
                    {errors[index].name && (
                      <span className="invalid-feedback">
                        {errors[index].name}
                      </span>
                    )}
                  </td>
                  <td>
                    <Input
                      type="text"
                      value={item.value}
                      name="value"
                      // type="number"
                      onChange={(e) => handleChange(e, index)}
                      onBlur={handleBlur(index)}
                      className={errors[index].value && "is-invalid"}
                    ></Input>
                    {errors[index].value && (
                      <span className="invalid-feedback">
                        {errors[index].value}
                      </span>
                    )}
                  </td>
                  <td>
                    <Input
                      type="text"
                      value={item.weight}
                      name="weight"
                      onChange={(e) => handleChange(e, index)}
                      onBlur={handleBlur(index)}
                      className={errors[index].weight && "is-invalid"}
                    ></Input>
                    {errors[index].weight && (
                      <span className="invalid-feedback">
                        {errors[index].weight}
                      </span>
                    )}
                  </td>
                  <td>
                    <Input
                      type="text"
                      value={item.stock}
                      name="stock"
                      onChange={(e) => handleChange(e, index)}
                      onBlur={handleBlur(index)}
                      className={errors[index].stock && "is-invalid"}
                    ></Input>
                    {errors[index].stock && (
                      <span className="invalid-feedback">
                        {errors[index].stock}
                      </span>
                    )}
                  </td>
                  <td>
                    <Input
                      type="text"
                      disabled
                      value={item.qty}
                      name="qty"
                      onChange={(e) => handleChange(e, index)}
                    ></Input>
                  </td>
                </tr>
              ))}
            </tbody>
          )}
        </Table>
        <Button onClick={() => clearItem()} color="warning">
          Clear
        </Button>{" "}
        <Button onClick={() => addItem()} color="info">
          Add Item
        </Button>{" "}
        <Button onClick={onSubmit} color="success">
          Greedy
        </Button>
      </Forms>
    </div>
  );
}

TableItem.propTypes = {};

export default TableItem;
